mapper.select:
	SELECT sido_code, content_type_id, title, latitude, longitude, addr1
    FROM attraction_info
    WHERE sido_code = #{sido} 
    	and content_type_id = #{contentId} 
    	and title like concat('%', #{searchKeyWord}, '%')
    	order by binary(title) asc

